package spm.kotlin.world.collections.firstLast/** * These functions return the first and the last element of the collection correspondingly. * You can also use them with a predicate; in this case, * they return the first or the last element that matches the given predicate.If a collection is empty or doesn't contain elements matching the predicate,the functions throw NoSuchElementException. */fun main() {    val numbers = listOf(1, -2, 3, -4, 5, -6)            // 1    val first = numbers.first()                          // 2    val last = numbers.last()                            // 3    val firstEven = numbers.first { it % 2 == 0 }        // 4    val lastOdd = numbers.last { it % 2 != 0 }           // 5}