package spm.kotlin.world.collections.set/** *A set is an unordered collection that does not support duplicates. For creating sets, * there are functions setOf() and mutableSetOf(). *  A read-only view of a mutable set can be obtained by casting it to Set. */val openIssues: MutableSet<String> = mutableSetOf("uniqueDescr1", "uniqueDescr2", "uniqueDescr3") // 1fun addIssue(uniqueDesc: String): Boolean {    return openIssues.add(uniqueDesc)                                                             // 2}fun getStatusLog(isAdded: Boolean): String {    return if (isAdded) "registered correctly." else "marked as duplicate and rejected."          // 3}fun main() {    val aNewIssue: String = "uniqueDescr4"    val anIssueAlreadyIn: String = "uniqueDescr2"    println("Issue $aNewIssue ${getStatusLog(addIssue(aNewIssue))}")                              // 4    println("Issue $anIssueAlreadyIn ${getStatusLog(addIssue(anIssueAlreadyIn))}")                // 5}