package spm.kotlin.world.enumClasses/** * Enum classes are used to model types that represent a finite set of distinct values, * such as directions, states, modes, and so forth. * */enum class State {    IDLE, RUNNING, FINISHED                           // 1}fun main() {    val state = State.RUNNING                         // 2    val message = when (state) {                      // 3        State.IDLE -> "It's idle"        State.RUNNING -> "It's running"        State.FINISHED -> "It's finished"    }    println(message)}